!function(){"use strict";class o{constructor(){this.buttonEl=document.querySelector(".order__button"),this.inputPhoneEl=document.querySelector(".order .mask-phone"),this.errorPhone=document.querySelector(".order__error-phone")}addButtonClickListener(){this.buttonEl.addEventListener("click",()=>{""==this.inputPhoneEl.value?this.errorPhone.style.display="block":this.errorPhone.style.display="none"})}addMaskPhone(){$(".order .mask-phone").mask("+375 (99) 999-99-99"),$(".order .mask-phone").on("keyup",function(){12==$(".order .mask-phone").val().match(/\d+/g).join("").length&&$(".order__error-phone").css("display","none")})}}window.addEventListener("load",()=>{var e=new o;e.addButtonClickListener(),e.addMaskPhone()})}();